<template>
  <div class="EditGUI noScrollbar">
    <el-alert type="info" show-icon :closable="false">
      参考文档：<a href="https://linux.die.net/man/8/fancontrol" target="_blank">linux.die.net/man/8/fancontrol</a>
    </el-alert>
    <el-form label-width="auto">
      <el-space fill style="display: flex;">
        <el-alert type="info" show-icon :closable="false">主循环的间隔（以秒为单位）</el-alert>
        <el-form-item label="检测间隔">
          <el-input-number v-model="gui.INTERVAL" :min="1" :step="1" :precision="0" step-strictly controls-position="right" >
            <template #suffix>
              <span>秒</span>
            </template>
          </el-input-number>
        </el-form-item>
      </el-space>
      <el-form-item label="传感器">
        <div class="smpleTable" v-if="gui">
          <el-button :icon="Plus" circle @click="openAddDevWindow"/>
          <el-dialog v-model="isOpenAddDev" title="添加Dev传感器">
            <el-form label-width="auto">
              <el-form-item label="传感器代号">
                <el-input v-model="newDevNumber" />
              </el-form-item>
            </el-form>
            <template #footer>
              <el-button type="primary" @click="addDev">添加</el-button>
            </template>
          </el-dialog>

          <el-table :data="Object.keys(gui?.dev)"><!--  style="width: 100%" -->
            <el-table-column prop="date" label="传感器代号" >
              <template #default="scope">
<!--                <el-input v-model="scope.row" @change="changeDevNumber" />-->
                <span>{{ scope.row }}</span>
              </template>
            </el-table-column>
            <el-table-column label="路径">
              <template #default="scope">
                <el-input v-model="gui.dev[scope.row].path" />
              </template>
            </el-table-column>
            <el-table-column label="名称" >
              <template #default="scope">
                <el-input v-model="gui.dev[scope.row].name" />
              </template>
            </el-table-column>
            <el-table-column label="删除" width="70">
              <template #default="scope">
                <el-button type="danger" text bg :icon="Delete" @click="delDev(scope.row)" />
              </template>
            </el-table-column>
          </el-table>
        </div>
      </el-form-item>
    </el-form>

