      <el-dialog v-model="isOpenAddFan" title="添加风扇">
        <el-form label-width="auto">
          <el-form-item label="风扇代号">
            <el-input v-model="newFanName" />
          </el-form-item>
        </el-form>
        <template #footer>
          <el-button type="primary" @click="addFan">添加</el-button>
        </template>
      </el-dialog>
    </div>

    <item-form v-if="nowSel!==''" v-model:item-data="gui.Fan[nowSel]" />
  </div>
</template>
<script>
import {computed} from "vue";
import {Delete, Plus, Search} from "@element-plus/icons-vue";
import {ElMessage, ElMessageBox} from "element-plus";
import ItemForm from "./ItemForm.vue";

export default {
  name: "EditGUI",
  computed: {
    Plus() {
      return Plus
    },
    Search() {
      return Search
    },
    Delete() {
      return Delete
    }
  },
  //引入模块
  components: {ItemForm},
  //父级传入数据
  props: {
    guiData: Object,
  },
  data() {
    return {
      //双向绑定处理
      gui: computed({
        get: () => this.guiData,
        set: val => {
          this.$emit("update:gui-data", val);
        }
      }),

      nowSel: "",

      isOpenAddDev: false,
      newDevNumber: "",

      isOpenAddFan: false,
      newFanName: "",
    }
  },
  //方法
  methods: {
    openAddDevWindow: function () {
      this.newDevNumber = ""
      this.isOpenAddDev = true
    },
    addDev: function () {
      if(this.gui.dev[this.newDevNumber]){
        ElMessage.error("传感器已存在")
        return
      }
      this.gui.dev[this.newDevNumber] = {}
      this.isOpenAddDev = false
    },
    delDev: function (dev) {
      ElMessageBox.confirm(
          '确实删除该传感器？',
          '警告',
          {
            confirmButtonText: '删除',
            cancelButtonText: '取消',
            type: 'warning',
          }
      )
          .then(() => {
            delete this.gui.dev[dev]
          })
          .catch(() => {
          })
    },
    openAddFanWindow: function () {
      this.newFanName = ""
      this.isOpenAddFan = true
    },
    addFan: function () {
      if(this.gui.Fan[this.newFanName]){
        ElMessage.error("风扇已存在")
        return
      }
      this.gui.Fan[this.newFanName] = {
        // 风扇路径
        FCFANS: "",
        // 读取传感器的温度
        FCTEMPS: "",
        // 低于多少度时处于最小速度
        MINTEMP: 0,
        // 高于多少度时处于最大速度
        MAXTEMP: 0,
        // 最大转速(0~255)
        MAXPWM: 0,
        // 最小转速
        MINPWM: 0,

        //
        MINSTOP: 0,

        //
        MINSTART: 0,
      }
      this.isOpenAddFan = false
    },
    delFan: function (fan) {
      ElMessageBox.confirm(
          '确实删除该风扇？',
          '警告',
          {
            confirmButtonText: '删除',
            cancelButtonText: '取消',
            type: 'warning',
          }
      )
        .then(() => {
          delete this.gui.Fan[fan]
        })
        .catch(() => {
        })
    },
  },
  //启动事件
  mounted() {
  },
  //销毁
  beforeUnmount() {
  }
}
</script>
<style scoped lang="scss">
.noScrollbar {
  /* 修改滚动条的宽度 */
  &::-webkit-scrollbar {
    width: 0px; /* 纵向滚动条的宽度 */
    height: 0px; /* 横向滚动条的高度 */
  }
  /* 兼容火狐 */
  scrollbar-width: none;
}

.EditGUI{
  display: flex;
  flex-direction: column;
  gap: 0.5rem;

  overflow-y: scroll;

  &>form{
    //flex-grow: 1;
  }
  &>div{
    flex-shrink: 0;
  }

  .smpleTable{
    width: 100%;
  }

  .fans{
    background: var(--el-color-success-light-5);
    padding: 1.5rem 1rem;
    border-radius: 0.5rem;

    flex-wrap: wrap;

    .fan{
      height: 100px;
      //width: 100px;
      display: flex;
      flex-direction: column;

      border: solid 2px transparent;
      padding: 1rem 0.5rem;
      border-radius: 0.5rem;

      .title{
        color: white;
        margin-bottom: 0.5rem;
      }
      svg{
        fill: #ffffff;
        //max-height: 100%;
        //max-width: 100%;
        height: 70px;
        //width: 70px;
        width: 100%;
      }

    }
    .fan.sel{
      border: solid 2px var(--el-color-primary);

      .title, svg{
        color: var(--el-color-primary);
        fill: var(--el-color-primary);
      }
    }
  }
}
</style>